mapper.update:
update deliver
set  state = #{status}
where id in

    #{c}

mapper.select:
select d.id, d.customer_id, d.phone, d.city, d.destination, d.state, d.third_company_id, d.third_company_pay, d.deliver_cost, d.deliver_time, d.express_company, d.express_no, d.create_uid, d.remark, d.product, d.quantity, d.third_company_type, d.no from deliver AS d
and d.city LIKE CONCAT('%',#{city},'%')
and d.destination LIKE CONCAT('%',#{destination},'%')
and d.express_company LIKE CONCAT('%',#{express_company},'%')
and d.express_no LIKE CONCAT('%',#{expressNo},'%')
and d.deliver_time BETWEEN #{createTimeStart} and #{createTimeEnd}
and d.create_uid = #{createUid}
and d.state = #{state}

SELECT d.id, d.customer_id, d.phone, d.city, d.province, d.destination, d.remark, d.deliver_cost, d.deliver_time, d.create_time, d.express_company, d.express_no, d.point_id, d.product, d.quantity, d.state, c.name as customer_name, p.name as point_name FROM deliver AS d LEFT JOIN customer AS c ON d.customer_id = c.id LEFT JOIN point AS p ON p.id = d.point_id
and d.city = #{query.city}
and d.customer_id = #{query.customerId}
and d.point_id = #{query.pointId}
and d.express_company = #{query.expressCompany}
and d.express_no LIKE CONCAT('%',#{query.expressNo},'%')
and d.deliver_time BETWEEN #{query.deliverTimeStart} and #{query.deliverTimeEnd}
ORDER BY d.create_time DESC
