mapper.select:
select
id, pn, material_alias, association_status, sort, supplier_id
from t_export_material_config


        and id = #{entity.id}


        and pn = #{entity.pn}


        and material_alias = #{entity.materialAlias}


        and association_status = #{entity.associationStatus}


        and sort = #{entity.sort}


        and supplier_id = #{entity.supplierId}


limit #{offset}, #{size}

select count(1)
from t_export_material_config


        and id = #{id}


        and pn = #{pn}


        and material_alias = #{materialAlias}


        and association_status = #{associationStatus}


        and sort = #{sort}


        and supplier_id = #{supplierId}
