mapper.select:
select
id,material_sn,product_no,del_flag,binding_status,create_time,update_time,tenant_id
from t_material

    del_flag = 0

        and id = #{query.id}


        and material_sn like concat('%',#{query.materialSn},'%')


        and product_no like concat('%',#{query.productNo},'%')


        and binding_status = #{query.bindingStatus}


        and create_time >= #{query.startTime}


        and create_time <= #{query.endTime}


        and tenant_id = #{query.tenantId}


limit #{offset}, #{size}

select count(1)
from t_material

    del_flag = 0

        and id = #{id}


        and material_sn like concat('%',#{materialSn},'%')


        and product_no like concat('%',#{productNo},'%')


        and binding_status = #{bindingStatus}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


        and tenant_id = #{tenantId}

select
id,material_sn,product_no,del_flag,binding_status,create_time,update_time,tenant_id
from t_material

    del_flag = 0

        and id = #{id}


        and material_sn = #{materialSn}


        and product_no = #{productNo}


        and binding_status = #{bindingStatus}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


        and tenant_id = #{tenantId}

mapper.update:
update t_material

    product_no = #{productNo},
    binding_status = #{bindingStatus},
    update_time = #{updateTime}

where id = #{id} and tenant_id = #{tenantId}
