mapper.select:
select
tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tm.tenant_id,
imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn
from t_material tm left join t_parts tp on tp.id = tm.material_id

    tm.del_flag  = 0

        and tm.id = #{query.id}


        and material_sn like concat('%',#{query.materialSn},'%')


        and product_no like concat('%',#{query.productNo},'%')


        and tp.sn = #{query.sn}


        and tp.name like concat('%',#{query.name},'%')


        and tmb.material_batch_no like concat('%',#{query.imei},'%')


        and material_batch_no like concat('%',#{query.materialBatchNo},'%')


        and material_state = #{query.materialState}


        and tm.material_id = #{query.materialId}


        and binding_status = #{query.bindingStatus}


        and tm.create_time >= #{query.startTime}


        and tm.create_time <= #{query.endTime}


        and tm.tenant_id = #{query.tenantId}


order by tm.id desc limit #{offset}, #{size}

select count(1)
from t_material tm left join t_parts tp on tp.id = tm.material_id

    tm.del_flag  = 0

        and tm.id = #{query.id}


        and material_sn like concat('%',#{query.materialSn},'%')


        and product_no like concat('%',#{query.productNo},'%')


        and tp.sn = #{query.sn}


        and tp.name like concat('%',#{query.name},'%')


        and tmb.material_batch_no like concat('%',#{query.imei},'%')


        and material_batch_no like concat('%',#{query.materialBatchNo},'%')


        and material_state = #{query.materialState}


        and tm.material_id = #{query.materialId}


        and binding_status = #{query.bindingStatus}


        and tm.create_time >= #{query.startTime}


        and tm.create_time <= #{query.endTime}


        and tm.tenant_id = #{query.tenantId}

select
tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id,
imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn
from t_material tm left join t_parts tp on tp.id = tm.material_id

    tm.del_flag  = 0

        and tm.id = #{id}


        and material_sn = #{materialSn}


        and product_no = #{productNo}


        and binding_status = #{bindingStatus}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


        and tenant_id = #{tenantId}

select material_sn
from t_material
where product_no = #{productNo}
limit #{offset}, #{size}

select tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
tm.del_flag = 0
#{batch}

select tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
tm.del_flag = 0
#{batch}

select tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
tm.del_flag = 0
#{batch}

select tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
binding_status = 1 and tm.del_flag = 0
#{batch}
limit 1;

select tm.id,material_sn,product_no,tm.del_flag ,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
tm.del_flag = 0
#{batch}

select tm.id,material_sn,product_no,tm.del_flag,binding_status,tm.create_time,tm.update_time,tenant_id, imei,atmel_id,test_time,remark,material_batch_no,material_state,material_id,tp.name,tp.sn from t_material tm left join t_parts tp on tp.id = tm.material_id
tm.del_flag = 0 and tp.sn is not null
#{batch}

select count(1) as count,material_batch_no from t_material

    del_flag  = 0

        #{batchNo}


        and material_state = #{unPassing}


group by material_batch_no

mapper.update:
update t_material
product_no = #{productNo}, binding_status = #{bindingStatus}, update_time = #{updateTime}, tenant_id = 0
where id = #{id} and tenant_id = #{tenantId}

update t_material

    del_flag = 1,
    update_time = #{updateTime}

where id = #{id}

update t_material
material_state = #{materialState}, update_time = #{updateTime}, remark = #{remark}
product_no = null
where del_flag = 0 and id in
#{id}
