mapper.select:
select
id, name , customer_id, status, city_id, install_type, address, camera_count, canopy_count, sn_no, card_number,
remarks,create_uid,create_time, del_flag, card_supplier, completion_time, is_entry, is_acceptance, order_source,
order_time, operator, logistics_info, product_info, camera_info, install_time
from t_point_new

and del_flag = 0

        and name like concat('%', #{name},'%')


        and customer_id = #{customerId}


        and status = #{status}


        and city_id = #{cid}


        and sn_no = #{snNo}


        and create_time >= #{startTime}



        and create_time <= #{endTime}


        and create_uid = #{createUid}


order by create_time desc
limit #{offset}, #{limit}

select
id, name , customer_id, status, city_id, install_type, address, camera_count, canopy_count, sn_no, card_number,
remarks,create_uid,create_time, del_flag
from t_point_new


        and id = #{id}


        and name = #{name}


        and customer_id = #{customerId}


        and status = #{status}


        and city_id = #{cityId}


        and install_type = #{installType}


        and address = #{address}


        and camera_count = #{cameraCount}


        and canopy_count = #{canopyCount}


        and sn_no = #{snNo}


        and card_number = #{cardNumber}


        and create_time = #{createTime}


        and del_flag = #{delFlag}

select count(1) from t_point_new

    and del_flag = 0

        and name like concat('%', #{name},'%')


