SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_type as wo_third_company_type, wo.third_company_id as wo_third_company_id, wo.third_company_pay as wo_third_company_pay, wo.third_reason as wo_third_reason, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wo.transfer_source_point_id as wo_transfer_source_point_id, wo.transfer_destination_point_id as wo_transfer_destination_point_id, wo.product as wo_product, wo.code as wo_code, wo.reason as wo_reason, (SELECT name FROM point WHERE id = wo_transfer_source_point_id) as wo_transfer_source_point_name, (SELECT name FROM point WHERE id = wo_transfer_destination_point_id) as wo_transfer_destination_point_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name, f.id as f_id, f.type as f_type, f.file_name as f_file_name, f.bind_id as f_bind_id FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN point as p on p.id = wo.point_id LEFT JOIN file as f on f.bind_id = wo.id and f.type =2
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.status = #{query.status}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_type = #{query.thirdCompanyType}
and wo.create_time BETWEEN #{query.createTimeStart} and #{query.createTimeEnd}
ORDER BY wo.create_time DESC

SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_id as wo_third_company_id, wo.third_company_type as wo_third_company_type, wo.third_company_pay as wo_third_company_pay, wo.third_reason as wo_third_reason, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wo.transfer_source_point_id as wo_transfer_source_point_id, wo.transfer_destination_point_id as wo_transfer_destination_point_id, wo.product as wo_product, wo.code as wo_code, (SELECT name FROM point WHERE id = wo_transfer_source_point_id) as wo_transfer_source_point_name, (SELECT name FROM point WHERE id = wo_transfer_destination_point_id) as wo_transfer_destination_point_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name
FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN point as p on p.id = wo.point_id
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.status = #{query.status}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_type = #{query.thirdCompanyType}
and wo.create_time BETWEEN #{query.createTimeStart} and #{query.createTimeEnd}
ORDER BY wo.create_time DESC

SELECT FROM_UNIXTIME( create_time / 1000, '%Y-%m-%d' ) AS date, count( work_order_reason_id ) AS count, work_order_reason_id as reasonId FROM work_order WHERE work_order_reason_id IS NOT NULL
and create_time >= #{datestamp}
GROUP BY date,work_order_reason_id ORDER BY date DESC

SELECT sum( o.fee ) as sumCount ,b.city, count(1) as numCount FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and o.create_time >= #{datestamp}
and b.city = #{cityId}
GROUP BY b.city ORDER BY sumCount desc limit 20

SELECT sum( o.fee ) as sumCount ,b.name as pointName, count(1) as numCount FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and o.create_time >= #{datestamp}
and b.city = #{cityId}
GROUP BY b.id ORDER BY sumCount desc limit 20

SELECT FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ) AS date, sum( o.fee ) AS sumCount, count(1) as numCount, b.city FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and o.create_time >= #{datestamp}
and b.city = #{cityId}
GROUP BY date ORDER BY date DESC

SELECT SUM( o.fee ) AS sumCount, p.name as pointName,p.city,count(1) as numCount FROM work_order o LEFT JOIN point p ON o.point_id = p.id WHERE o.type IN ( 1, 2, 3 )
and o.create_time >= #{datestamp}
and p.city = #{cityId}
GROUP BY p.city ORDER BY sumCount DESC LIMIT 20

SELECT SUM( o.fee ) AS sumCount, p.NAME AS pointName, p.city, p.id as pointId, count( o.type) AS numCount, o.type FROM work_order o LEFT JOIN point p ON o.point_id = p.id WHERE 1 = 1
and o.create_time >= #{datestamp}
and p.city = #{cityId}
GROUP BY o.point_id,o.type ORDER BY sumCount DESC LIMIT 20

SELECT SUM( o.fee ) AS sumCount, p.NAME AS pointName, p.city, count( 1 ) AS numCount, FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ) AS date FROM work_order o LEFT JOIN point p ON o.point_id = p.id WHERE o.type IN ( 1, 2, 3 )
and o.create_time >= #{datestamp}
and p.city = #{cityId}
GROUP BY date ORDER BY date DESC LIMIT 20
