FROM work_order as wo LEFT JOIN work_order_type as wot on wot.type = wo.type LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id
and wo.point_id = #{query.pointId}
