SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_type as wo_third_company_type, wo.third_company_id as wo_third_company_id, wo.third_company_pay as wo_third_company_pay, wo.third_reason as wo_third_reason, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wo.transfer_source_point_id as wo_transfer_source_point_id, wo.transfer_destination_point_id as wo_transfer_destination_point_id, wo.product as wo_product, wo.code as wo_code, wo.reason as wo_reason, wo.describeinfo as wo_describeinfo, wo.payment_method as wo_payment_method, wo.third_payment_status as wo_third_payment_status, wo.destination_type as wo_destination_type, wo.third_responsible_person as wo_third_responsible_person, (SELECT name FROM t_point_new WHERE id = wo_transfer_source_point_id) as wo_transfer_source_point_name, (SELECT name FROM t_point_new WHERE id = wo_transfer_destination_point_id) as wo_transfer_destination_point_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name
FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN t_point_new as p on p.id = wo.point_id
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.status = #{query.status}
and wo.status in(1,2,3,5)
and wo.creater_id = #{query.createrId}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_type = #{query.thirdCompanyType}
and wo.payment_method = #{query.paymentMethod}
and wo.process_time BETWEEN #{query.processTimeStart} and #{query.processTimeEnd}
and wo.third_company_name like concat('%',#{query.thirdCompanyName},'%')
and wo.server_name like concat('%',#{query.serverName},'%')
and wo.work_order_reason_id = #{query.workOrderReasonId}
and wo.third_responsible_person like concat('%', #{query.thirdResponsiblePerson}, '%')
ORDER BY wo.create_time DESC

SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_id as wo_third_company_id, wo.third_company_type as wo_third_company_type, wo.third_company_pay as wo_third_company_pay, wo.third_reason as wo_third_reason, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wo.transfer_source_point_id as wo_transfer_source_point_id, wo.transfer_destination_point_id as wo_transfer_destination_point_id, wo.product as wo_product, wo.code as wo_code, wo.describeinfo as wo_describeinfo, wo.payment_method as wo_payment_method, wo.destination_type as wo_destination_type, wo.third_payment_status as wo_third_payment_status, wo.third_responsible_person as wo_third_responsible_person, (SELECT name FROM t_point_new WHERE id = wo_transfer_source_point_id) as wo_transfer_source_point_name, (SELECT name FROM t_point_new WHERE id = wo_transfer_destination_point_id) as wo_transfer_destination_point_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name
FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN t_point_new as p on p.id = wo.point_id
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.status = #{query.status}
and wo.status in(1,2,3,5)
and wo.creater_id = #{query.createrId}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_type = #{query.thirdCompanyType}
and wo.payment_method = #{query.paymentMethod}
and wo.process_time BETWEEN #{query.processTimeStart} and #{query.processTimeEnd}
and wo.third_company_name like concat('%',#{query.thirdCompanyName},'%')
and wo.server_name like concat('%',#{query.serverName},'%')
and wo.work_order_reason_id = #{query.workOrderReasonId}
and wo.third_responsible_person like concat('%', #{query.thirdResponsiblePerson}, '%')
ORDER BY wo.process_time DESC
LIMIT #{query.offset},#{query.size}

SELECT count( a.work_order_reason_id ) AS count, a.work_order_reason_id as reasonId FROM work_order a left join work_order_reason b on a.work_order_reason_id = b.id WHERE a.work_order_reason_id IS NOT NULL and b.parent_id = 9
and a.create_time BETWEEN #{startTime} and #{endTime}
GROUP BY a.work_order_reason_id LIMIT 20

SELECT FROM_UNIXTIME( a.create_time / 1000, '%Y-%m-%d' ) as dateTime, DATE_FORMAT(FROM_UNIXTIME( a.create_time / 1000, '%Y-%m-%d' ),'%X第%V周') AS date, count( a.work_order_reason_id ) AS count, a.work_order_reason_id as reasonId FROM work_order a left join work_order_reason b on a.work_order_reason_id = b.id WHERE a.work_order_reason_id IS NOT NULL and b.parent_id = 9
and a.create_time BETWEEN #{startTime} and #{endTime}
GROUP BY date,work_order_reason_id ORDER BY date DESC

SELECT b.city, count( 1 ) AS numCount, ( CASE WHEN o.third_company_pay IS NOT NULL THEN sum( o.fee - o.third_company_pay ) ELSE sum( o.fee ) END ) AS sumCount FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and b.city = #{cityId}
and o.create_time BETWEEN #{startTime} and #{endTime}
GROUP BY b.city ORDER BY sumCount DESC LIMIT 20

SELECT b.name as pointName, count(1) as numCount, ( CASE WHEN o.third_company_pay IS NOT NULL THEN sum( o.fee - o.third_company_pay ) ELSE sum( o.fee ) END ) AS sumCount FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and b.city = #{cityId}
and o.create_time BETWEEN #{startTime} and #{endTime}
GROUP BY b.id ORDER BY sumCount desc limit 20

SELECT FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ) as dateTime, DATE_FORMAT(FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ),'%X第%V周') AS date, -- count( a.work_order_reason_id ) AS count, -- FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ) AS date, sum( o.fee ) AS sumCount, count(1) as numCount, b.city FROM work_order o LEFT JOIN point b ON o.point_id = b.id WHERE o.type = 5
and o.create_time BETWEEN #{startTime} and #{endTime}
and b.city = #{cityId}
GROUP BY date ORDER BY date DESC

SELECT p.name as pointName,p.city,count(1) as numCount, ( CASE WHEN o.third_company_pay IS NOT NULL THEN sum( o.fee - o.third_company_pay ) ELSE sum( o.fee ) END ) AS sumCount FROM work_order o LEFT JOIN point p ON o.point_id = p.id WHERE o.type IN ( 1, 2, 3 )
and o.create_time BETWEEN #{startTime} and #{endTime}
and p.city = #{cityId}
GROUP BY p.city ORDER BY sumCount DESC LIMIT 20

SELECT p.NAME AS pointName, p.city_id, p.id as pointId, count(o.type) AS numCount, o.type, ( CASE WHEN o.third_company_pay IS NOT NULL THEN sum( o.fee - o.third_company_pay ) ELSE sum( o.fee ) END ) AS sumCount FROM work_order o LEFT JOIN t_point_new p ON o.point_id = p.id WHERE o.type IN ( 1, 2, 3 )
and o.create_time BETWEEN #{startTime} and #{endTime}
and p.city_id = #{cityId}
GROUP BY o.point_id,o.type ORDER BY sumCount DESC LIMIT 20

SELECT p.NAME AS pointName, p.city, count( 1 ) AS numCount, FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ) as dateTime, DATE_FORMAT(FROM_UNIXTIME( o.create_time / 1000, '%Y-%m-%d' ),'%X第%V周') AS date, ( CASE WHEN o.third_company_pay IS NOT NULL THEN sum( o.fee - o.third_company_pay ) ELSE sum( o.fee ) END ) AS sumCount FROM work_order o LEFT JOIN point p ON o.point_id = p.id WHERE o.type IN ( 1, 2, 3 )
and o.create_time BETWEEN #{startTime} and #{endTime}
and p.city = #{cityId}
GROUP BY date ORDER BY date DESC LIMIT 20

SELECT count(1)
FROM work_order as wo
LEFT JOIN work_order_type as wot on wot.id = wo.id
LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id
LEFT JOIN user u on u.id =wo.creater_id
LEFT JOIN point as p on p.id = wo.point_id


        and wo.point_id = #{query.pointId}


        and wo.type = #{query.type}


        and wo.status = #{query.status}


        and wo.order_no = #{query.orderNo}


        and wo.third_company_id = #{query.thirdCompanyId}


        and wo.third_company_type = #{query.thirdCompanyType}



        and wo.process_time BETWEEN #{query.processTimeStart} and #{query.processTimeEnd}


ORDER BY wo.create_time DESC
