mapper.select:
SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_type as wo_third_company_type, wo.third_company_id as wo_third_company_id, wo.third_company_pay as wo_third_company_pay, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name, f.id as f_id, f.type as f_type, f.file_name as f_file_name, f.bind_id as f_bind_id FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN point as p on p.id = wo.point_id LEFT JOIN file as f on f.bind_id = wo.id and f.type =2
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.starus = #{query.status}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_Type = #{query.thirdCompanyType}
and wo.create_time BETWEEN #{query.createTimeStart} and #{query.createTimeEnd}
ORDER BY wo.create_time DESC

SELECT wo.id as wo_id, wo.type as wo_type, wo.point_id as wo_point_id, wo.info as wo_info, wo.fee as wo_fee, wo.third_company_type as wo_third_company_type, wo.third_company_id as wo_third_company_id, wo.third_company_pay as wo_third_company_pay, wo.work_order_reason_id as wo_work_order_reason_id, wo.processor as wo_processor, wo.creater_id as wo_creater_id, wo.process_time as wo_process_time, wo.status as wo_status, wo.order_no as wo_order_no, wo.create_time as wo_create_time, wo.server_id as wo_server_id, wo.third_company_name as wo_third_company_name, wo.server_name as wo_server_name, wot.type as wot_type, wor.name as wor_name, u.user_name as u_user_name, p.name as p_name
FROM work_order as wo LEFT JOIN work_order_type as wot on wot.id = wo.id LEFT JOIN work_order_reason as wor on wor.id = wo.work_order_reason_id LEFT JOIN user u on u.id =wo.creater_id LEFT JOIN point as p on p.id = wo.point_id
and wo.point_id = #{query.pointId}
and wo.type = #{query.type}
and wo.starus = #{query.status}
and wo.order_no = #{query.orderNo}
and wo.third_company_id = #{query.thirdCompanyId}
and wo.third_company_Type = #{query.thirdCompanyType}
and wo.create_time BETWEEN #{query.createTimeStart} and #{query.createTimeEnd}
ORDER BY wo.create_time DESC
